import React, {useLayoutEffect} from 'react'
import {
    View,
    Text,
    Image,
    TouchableOpacity,
    StyleSheet
} from 'react-native'
import { Ionicons } from '@expo/vector-icons';


const DetailProductScreen = ({navigation}) => {

    useLayoutEffect(() => {
        navigation.setOptions({
            title: "SHOP PRODUCTS",
            headerTitleStyle: { color: "blue" },
            headerRight: () => (
                <View style={{marginRight : 5}}>
                    <TouchableOpacity style={{display : flex}} onPress={() => navigation.navigate("Cart")}>
                        <Ionicons name="cart-outline" size={25} color="black" />
                        <span>0</span>
                    </TouchableOpacity>
                </View>
            )
        })
    }, [navigation])

    return (
        <View style={styles.container}>
            <View>
                <Image
                    resizeMode="cover"
                    style={{
                        paddingTop: 5,
                        right: 0,
                        height: 200,
                        width: "98%"
                    }}
                    source={{
                        uri: 'https://res.cloudinary.com/http-codingcampus-pythonanywhere-com/image/upload/v1615399961/test/lwmgl7wiob0ptb8wrfq5.png'
                    }}
                />
            </View>
            <View style={styles.detail_sec}>
                <Text>hello Ashutosh kumar this is detail screen</Text>
            </View>
        </View>
    )
}

export default DetailProductScreen


const styles = StyleSheet.create({
    container: {
        flex: 1,
        paddingTop: 2,
    },
    detail_sec : {
        flex : 1,
        justifyContent : "flex-start"
    }
})
