import React, { useLayoutEffect } from 'react'
import {
    View,
    Text,
    Image,
    TouchableOpacity,
    StyleSheet
} from 'react-native'
import { Ionicons } from '@expo/vector-icons';


const DetailProductScreen = ({ navigation }) => {

    useLayoutEffect(() => {
        navigation.setOptions({
            title: "Detail Product",
            headerTitleStyle: { color: "blue" },
            headerRight: () => (
                <View style={{ marginRight: 5 }}>
                    <TouchableOpacity onPress={() => navigation.navigate("Cart")}>
                        <Ionicons name="cart-outline" size={25} color="black" />
                    </TouchableOpacity>
                </View>
            )
        })
    }, [navigation])

    return (
        <View style={styles.container}>
            <View>
                <Image
                    resizeMode="cover"
                    style={{
                        paddingTop: 5,
                        right: 0,
                        height: 200,
                        width: "98%"
                    }}
                    source={{
                        uri: 'https://res.cloudinary.com/http-codingcampus-pythonanywhere-com/image/upload/v1615399961/test/lwmgl7wiob0ptb8wrfq5.png'
                    }}
                />
            </View>
            <View style={styles.detail_sec}>
                <View style={styles.subDetail}>
                    <Text>Ashutosh kumar maurya</Text>
                </View>
                <View style={styles.subDetail}>
                    <Text>Ashuto</Text>
                </View>
                <View style={styles.subDetail}>
                    <Text>Ashutosh kumar maurya</Text>
                </View>
            </View>
        </View>
    )
}

export default DetailProductScreen


const styles = StyleSheet.create({
    container: {
        flex: 1,
        paddingTop: 2,
    },
    detail_sec: {
        flex: 1,
        paddingTop: 5,
    },
    subDetail: {
        width: "92%",
        borderRadius: 10,
        marginLeft: 10,
        marginRight: 10,
        marginBottom : 5
    }
})
